mapper.select:
SELECT 
	* 
FROM
	(SELECT 
		* 
	FROM
		(SELECT b.* FROM user_assistant WHERE assistant=#{assistant}) a 
	LEFT JOIN 
		`order` b ON a.uid=b.uid) c

	
		`id`=#{id}
	
	
		AND `uid`=#{uid}
	
	
		AND `state`=#{state}
	
	
		 AND `deliver_stop`<=#{deliverStop}      
	
	
		 AND `deliver_start`>=#{deliverStart}      
	
	
		 AND `created`<=#{timeStop}      
	
	
		 AND `created`>=#{timeStart}      
	

ORDER BY `created` DESC
