mapper.select:
SELECT * FROM (
	SELECT 
		c.`uid`, 
		c.`type`, 
		c.`username`,
		c.`created` bind_time,
		b.`nickname`,
		b.`created` register_time,
		d.`token`,
		d.`os`,
		d.`type` device_type,
		d.`client`,
		d.`created` login_time 
	FROM (
		SELECT * FROM user_assistant WHERE assistant=#{assistant}
	) a 
	LEFT JOIN 
		user_info b ON a.uid=b.id
	LEFT JOIN 
		username c ON a.uid=c.uid 
	LEFT JOIN 
		user_device d ON c.id=d.username
) e

	
		`os`=#{os}
	
	
		AND `uid`=#{uid}
	
	
		AND `client`=#{client}
	
	
		AND `device_type`=#{deviceType}
	
	
		AND `type`=#{usernameType}
	
	
		 AND `register_time`<=#{registerTimeEnd}      
	
	
		 AND `register_time`>=#{registerTimeBegin}      
	
	
		AND `nickname` LIKE %#{nickname}%
	
	
		AND `username` LIKE %#{username}%
